def common(ind1, ind2):
    i = 0
    size = min(len(ind1), len(ind2))
    features = []
    while i < size:
        if ind1[i] == 1 & ind2[i] == 1:
            features.append(i)
        i += 1
    print("2 共同特征："+str(len(features)))
    print(features)

def common(ind1, ind2, ind3):
    i = 0
    size = min(len(ind1), len(ind2), len(ind3))
    features = []
    while i < size:
        if ind1[i] == 1 & ind2[i] == 1 & ind3[i]:
            features.append(i)
        i += 1
    print("3 共同特征："+str(len(features)))
    print(features)

def compare(ind1, ind2):
    i = 0
    size = min(len(ind1), len(ind2))
    features = []
    while i < size:
        if ind1[i] > ind2[i]:
            features.append(i)
        i += 1
    print("特征："+str(len(features)))
    print(features)

